//import React library for creating components
import React from "react";
//import List component
import List from "../List/index.jsx";
//import addList icon
import addSvg from "../../assets/img/add.svg";
//import styles
import "./AddList.scss";
//import Badge component
import Badge from "../Badge/index.jsx";
//import close icon
import closeSvg from "../../assets/img/close.svg";

const AddListButton = ({ colors, onAdd }) => {
  //useState hook for hiding/showing add-list popup menu
  const [visiblePopup, setVisiblePopUp] = React.useState(false);
  //useState hook for showing selected color
  const [selectedColor, setSelectColor] = React.useState(colors[0].id);
  //useState hook for setting text value in input
  const [inputValue, setInputValue] = React.useState("");

  //function which creates new list by clicking on button
  const addList = () => {
    if (!inputValue) {
      alert("Enter name of list");
      return;
    }
    const color = colors.filter((c) => c.id === selectedColor)[0].name;
    onAdd({
      id: Math.random(),
      name: inputValue,
      color,
    });
    // аfter adding new list hides popup menu
    setVisiblePopUp(false);
    //resets input value
    setInputValue("");
  };

  return (
    <div className="add-list">
      <List
        onClick={() => setVisiblePopUp(!visiblePopup)}
        items={[
          {
            className: "list__add-button",
            icon: <img src={addSvg} alt="addList icon" />,
            name: "Add List",
          },
        ]}
      />
      {visiblePopup && (
        <div className="add-list-popup">
          <img
            onClick={() => setVisiblePopUp(false)}
            className="add-list__popup-close-btn"
            src={closeSvg}
            alt="Close Icon"
          />

          <input
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            className="field"
            type="text"
            placeholder="Name of List"
          />
          <div className="add-list__popup-colors">
            {colors.map((color) => (
              <Badge
                onClick={() => setSelectColor(color.id)}
                key={color.id}
                color={color.name}
                className={selectedColor === color.id && "active"}
              />
            ))}
          </div>
          <button onClick={addList} className="button">
            Add
          </button>
        </div>
      )}
    </div>
  );
};

export default AddListButton;
