import React from "react";
// import styles
import "./Tasks.scss";
//import edit icon
import editSvg from "../../assets/img/edit.svg";
//import library for working with requests
import axios from "axios";
//import AddTaskForm component
import AddTaskForm from "./AddTaskFrom";

const Tasks = ({ list, onEditTitle, onAddTask }) => {
  //function that changes title
  const editTitle = () => {
    //new title witch is taken from user
    const newTitle = window.prompt("Name of list", list.name);
    //function that changes title will only work in case if user еntered new name
    if (newTitle) {
      onEditTitle(list.id, newTitle);
      //changes title on Back-end
      axios
        .patch("http://localhost:3001/lists/" + list.id, {
          name: newTitle,
        })
        .catch(() => {
          alert("Cant update item title");
        });
    }
  };

  return (
    <div className="tasks">
      <h2 className="tasks__title">
        {list.name}
        {/* Edit list name button */}
        <img onClick={editTitle} src={editSvg} alt="edit icon" />
      </h2>

      <div className="tasks__items">
        {/* if there is no tasks in list item displays text */}
        {!list.tasks.length && <h2>There are no tasks</h2>}
        {list.tasks.map((task) => (
          <div key={task.id} className="tasks__items-row">
            <div className="checkbox">
              <input id={`task-${task.id}`} type="checkbox" />
              <label htmlFor={`task-${task.id}`}>
                <svg
                  width="11"
                  height="8"
                  viewBox="0 0 11 8"
                  fill="none"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    d="M9.29999 1.20001L3.79999 6.70001L1.29999 4.20001"
                    stroke="black"
                    stroke-width="1.5"
                    stroke-linecap="round"
                    stroke-linejoin="round"
                  />
                </svg>
              </label>
            </div>
            <input value={task.text} readOnly />
          </div>
        ))}
      </div>
      <AddTaskForm list={list} onAddTask={onAddTask} />
    </div>
  );
};

export default Tasks;
