import React from "react";
// import styles
import "./Tasks.scss";
//import edit icon
import editSvg from "../../assets/img/edit.svg";
//import library for working with requests
import axios from "axios";
//import AddTaskForm component
import AddTaskForm from "./AddTaskFrom";
//import Task item component
import Task from "./Task";

const Tasks = ({
  list,
  onEditTitle,
  onAddTask,
  withoutEmpty,
  onRemoveTask,
}) => {
  //function that changes title
  const editTitle = () => {
    //new title witch is taken from user
    const newTitle = window.prompt("Name of list", list.name);
    //function that changes title will only work in case if user еntered new name
    if (newTitle) {
      onEditTitle(list.id, newTitle);
      //changes title on Back-end
      axios
        .patch("http://localhost:3001/lists/" + list.id, {
          name: newTitle,
        })
        .catch(() => {
          alert("Cant update item title");
        });
    }
  };

  return (
    <div className="tasks">
      <h2 style={{ color: list.color.hex }} className="tasks__title">
        {list.name}
        {/* Edit list name button */}
        <img onClick={editTitle} src={editSvg} alt="edit icon" />
      </h2>

      <div className="tasks__items">
        {/* if there is no tasks in list item displays text */}
        {!withoutEmpty && !list.tasks.length && <h2>There are no tasks</h2>}
        {list.tasks.map((task) => (
          <Task
            key={task.id}
            list={list}
            onEdit={onEdit}
            onRemove={onRemoveTask}
            {...task}
          />
        ))}
      </div>
      <AddTaskForm list={list} onAddTask={onAddTask} />
    </div>
  );
};

export default Tasks;
