//import React for Creating components
import React, { useState, useEffect } from "react";
//import list icon
import listSvg from "./assets/img/list.svg";
//import database
import DB from "./assets/db.json";
//import all components
import { List, AddListButton, Tasks } from "./components/index.jsx";
//import libary for making requests to json server
import axios from "axios";

function App() {
  //useState hook that changes state of lists array
  const [lists, setLists] = useState(
    DB.lists.map((item) => {
      item.color = item.color = DB.colors.filter(
        (color) => color.id === item.colorId
      )[0].name;
      return item;
    })
  );

  // useEffect hook checks if app component was rendered and in that case make get request
  useEffect(() => {
    //get request to json server
    axios.get("http://localhost:3001/lists?_expand=color").then(({ data }) => {
      console.log(data);
    });
  }, [lists]);

  //function that adds created list to array of all lists
  const onAddList = (obj) => {
    //using spread operator in order to add created list to old array and create newArray
    const newList = [...lists, obj];
    setLists(newList);
  };

  return (
    <div className="todo">
      {/* sidebar block */}
      <div className="todo__sidebar">
        <List
          items={[
            {
              icon: <img src={listSvg} alt="list icon" />,
              name: "List of Tasks",
            },
          ]}
        />
        <List items={lists} onRemove={() => alert(1)} isRemovable />
        <AddListButton onAdd={onAddList} colors={DB.colors} />
      </div>
      {/* tasks block */}
      <div className="todo__tasks">
        <Tasks />
      </div>
    </div>
  );
}

export default App;
