//import React for Creating components
import React, { useState } from "react";
//import List component
import List from "./components/List/index.jsx";
//import list icon
import listSvg from "./assets/img/list.svg";
//import addButtonList component
import AddListButton from "./components/AddList/index.jsx";
//import database
import DB from "./assets/db.json";

function App() {
  //useState hook
  const [value, setValue] = React.useState("");

  return (
    <div className="todo">
      {/* sidebar block */}
      <div className="todo__sidebar">
        <List
          items={[
            {
              icon: <img src={listSvg} alt="list icon" />,
              name: "List of Tasks",
            },
          ]}
        />
        <List
          items={DB.lists.map((item) => {
            item.color = item.color = DB.colors.filter(
              (color) => color.id === item.colorId
            )[0].name;
            return item;
          })}
          isRemovable
        />
        <AddListButton colors={DB.colors} />
      </div>
      {/* tasks block */}
      <div className="todo__tasks"></div>
    </div>
  );
}

export default App;
