import React, { useState } from "react";
//import add icon
import addSvg from "../../assets/img/add.svg";

const AddTaskForm = () => {
  //useState hook switches visibility of form
  const [visibleForm, setVisibleForm] = useState(false);

  // function that switches visibility state of form
  const toggleVisibleForm = () => {
    setVisibleForm(true);
  };

  return (
    <div className="tasks__form">
      {!visibleForm ? (
        <div onClick={toggleVisibleForm} className="tasks__form-new">
          <img src={addSvg} alt="Add icon" />
          <span>New Task</span>
        </div>
      ) : (
        <div className="tasks__form-block">
          <inpput className="field" type="text" placeholder="Task text" />
          <button className="button">Add Task</button>
          <button className="button button--grey">Cancel</button>
        </div>
      )}
    </div>
  );
};

export default AddTaskForm;
